mapper.insert:

	select user_info_seq.nextval as userIdx from dual

insert into 
user_info
(
	USER_IDX
	,USER_REGISTER_DATE
	,USER_NAME
	,USER_SOCIAL_SECUNUM 
	,USER_SEX
	,USER_COMP
	,USER_COMP_ENTERDATE 
	,USER_DEPT
	,USER_SPOT
	,USER_ARMY_SERV
	,USER_MARITAL_STATUS
	,USER_ARMY_SERV_ENTER
	,USER_ARMY_SERV_LEAVE
	,USER_ARMY_SERV_PERIOD
	,USER_TELNUM_WIRED
	,USER_TELNUM_WIRELESS
	,USER_EMAIL
	,USER_ZIPCODE
	,USER_ADDRESS
) 
values
(
	#{userIdx}
	,sysdate
	,#{userName}
	,#{userSocialSecunum}
	,#{userSex}
	,#{userComp}
	,sysdate
	,#{userDept}
	,#{userSpot}
	,#{userArmyServ}
	,#{userMaritalStatus}
	,sysdate
	,sysdate
	,#{userArmyServPeriod}
	,#{userTelnumWired}
	,#{userTelnumWireless}
	,#{userEmail}
	,#{userZipcode}
	,#{userAddress}
)

mapper.update:
UPDATE
USER_INFO SET				
	USER_REGISTER_DATE     = sysdate               
	,USER_NAME              = #{userName}           
	,USER_SOCIAL_SECUNUM    = #{userSocialSecunum}  
	,USER_SEX               = #{userSex}            
	,USER_COMP              = #{userComp}           
	,USER_COMP_ENTERDATE    = sysdate               
	,USER_DEPT              = #{userDept}           
	,USER_SPOT              = #{userSpot}           
	,USER_ARMY_SERV         = #{userArmyServ}       
	,USER_MARITAL_STATUS    = #{userMaritalStatus}  
	,USER_ARMY_SERV_ENTER   = sysdate               
	,USER_ARMY_SERV_LEAVE   = sysdate               
	,USER_ARMY_SERV_PERIOD  = #{userArmyServPeriod} 
	,USER_TELNUM_WIRED      = #{userTelnumWired}    
	,USER_TELNUM_WIRELESS   = #{userTelnumWireless} 
	,USER_EMAIL             = #{userEmail}          
	,USER_ZIPCODE           = #{userZipcode}        
	,USER_ADDRESS           = #{userAddress}
WHERE
	USER_IDX=#{userIdx}
