mapper.insert:


	select user_info_edu_seq.nextval from dual


insert into
user_info_${tbName}
(
	EDU_IDX
	,USER_IDX
	,EDU_SCHOOL_NAME
	,EDU_STATUS
	,EDU_GRADUATION
) 
values
(
	#{eduIdx}
	,#{userIdx}
	,#{eduSchoolName}
	,#{eduStatus}
	,#{eduGraduation}
)

mapper.update:
UPDATE
USER_INFO_EDU
SET
	EDU_SCHOOL_NAME	= #{eduSchoolName}  
	,EDU_STATUS 	= #{eduStatus}  
	,EDU_GRADUATION	= #{eduGraduation}  
WHERE 
	USER_IDX=#{userIdx}
